import React, { useState } from 'react';
import Join from '../Join/Join'
import './TextContainer.css';
import { Link } from 'react-router-dom';

const TextContainer = ({ users, room }) => {
    const [name, setName] = useState('')
    const [currentRoom, setcurrentRoom] = useState('')

  return (
    <div className="textContainer">
        <div>
            <h1 style={{fontSize: "42px"}}>Realtime Chat Application <span role="img" aria-label="emoji">ðŸ’¬</span></h1>
            <br/>
            <p style={{fontSize: "22px"}}>Invite your friends to join room <b style={{color: "red"}}>{room}</b> !</p>
            <br/>
            <p style={{fontSize: "22px"}}>or Join Another Room: </p>
            <div className="joinOuterContainer">
            <div className="joinInnerContainer">
                <h1 className="heading">Join a Room!</h1>
                <div><input type="text" placeholder="Name" onChange={(event) => setName(event.target.value)} className="joinInput"/></div>
                <div><input type="text" placeholder="Room" onChange={(event) => setCurrentRoom(event.target.value)} className="joinInput mt-20"/></div>
                <Link onClick={event => (!name || !room) ? event.preventDefault() : null} to={`/chat?name=${name}&room=${room}`}>
                    <button className="button mt-20" type="submit">Join Room</button>
                </Link>
            </div>
        </div>
        </div>
            <h2>Created with React, Express, Node and Socket.IO</h2>
    </div>
  );
};

export default TextContainer;
