import React from 'react';
import './Message.css';
import ReactEmoji from 'react-emoji';

const Message = ({ message, name }) => {
    let isSentByCurrentUser = false;

    const trimmedName = name.trim().toLowerCase();

    if (message.user === trimmedName) {
        isSentByCurrentUser = true;
    }

    return ( 
        isSentByCurrentUser
            ? (
                <div className="messageContainer justifyEnd">
                    <p className="sentText pr-10">{(message.user).charAt(0).toUppercase()} <p style={{paddingLeft: '5px', fontSize: '10px'}}>{new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true })}</p></p>
                    <div className="messageBox backgroundBlue">
                    <p className="messageText colorWhite">{ReactEmoji.emojify(message.text)}</p>
                    </div>
                </div>
            )
            : (
                <div className="messageContainer justifyStart">
                    <div className="messageBox backgroundLight">
                    <p className="messageText colorDark">{ReactEmoji.emojify(message.text)}</p>
                </div>
                    <p className="sentText pl-10">{message.user} <span style={{paddingLeft: '5px', fontSize: '10px'}}>{new Date().toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true })}</span></p>
                </div>
            )
    );
}

export default Message
