import React, { useState } from 'react';
import Join from '../Join/Join'
import './TextContainer.css';
import { Link } from 'react-router-dom';

const TextContainer = ({ users, room }) => {
    const [currentRoom, setCurrentRoom] = useState('')

  return (
    <div className="textContainer">
        <div>
            <h1 style={{fontSize: "42px"}}>Realtime Chat Application 
                <span role="img" aria-label="emoji">ðŸ’¬</span>
            </h1>
            <p style={{fontSize: "22px"}}>Invite your friends to join room 
                <b style={{color: "red"}}> {room}</b> !
            </p>
            <p style={{fontSize: "22px"}}>or Join Another Room: </p>
            <div className="joinInnerContainer">
                <div>
                    <input type="text" placeholder="Room" onChange={(event) => setCurrentRoom(event.target.value)} className=""/>
                </div>
                <Link 
                    onClick={event => (!currentRoom) ? event.preventDefault() : null} 
                    to={`/chat?name=${JSON.stringify(users)}&room=${currentRoom}`}
                >
                    <button className="" type="submit">Join Room</button>
                </Link>
        </div>
        </div>
            <h2>Created with React, Express, Node and Socket.IO</h2>
    </div>
  );
};

export default TextContainer;
