import React from 'react';
import ScrollToBottom from 'react-scroll-to-bottom'

const Messages = () => {

    return (
        <ScrollToBottom>
            {
                messages.map(message => <p>
                    <b>{message.user.charAt(0).toUpperCase() + message.user.slice(1)}:</b> 
                    {message.text}
                </p>
            )}
        </ScrollToBottom>
    );
}

export default Messages
