import React, { useState, useEffect } from 'react';
import queryString from 'query-string';
import io from 'socket.io-client';

let socket;

const Chat = ({ location }) => {
    const [name, setName] = useState('')
    const [room, setRoom] = useState('')
    const ENDPOINT = 'localhost:5000'

    useEffect((() => {
        // return url then turn into an object
        const { name, room } = queryString.parse(location.search) 

        socket = io(ENDPOINT); // pass endpoint/url to server which is located at localhost5000

        setName(name)
        setRoom(room)

        // on the event join, send name and room to server
        socket.emit('join', { name, room }, () => {
            // any code here gets runned if callback function gets called
        });

        return () => {
            // emit disconnect event
            socket.emit('')
        }

    }), [ENDPOINT, location.search]) // if any these 2 values change, re-render useEffect

    return (
        <h1>Chat</h1>
    )
}

export default Chat;
