import React from 'react';
import './TextContainer.css';
import { Link } from 'react-router-dom';

const TextContainer = ({ users, room }) => {

  return (
    <div className="textContainer">
        <div>
            <h1>
                ChatCord
                <img src="https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/73790822-f2ce-45aa-bcb9-9f89327637d6/ddei3yy-b7227286-c75a-406a-bd95-958072a59e97.png?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJ1cm46YXBwOiIsImlzcyI6InVybjphcHA6Iiwib2JqIjpbW3sicGF0aCI6IlwvZlwvNzM3OTA4MjItZjJjZS00NWFhLWJjYjktOWY4OTMyNzYzN2Q2XC9kZGVpM3l5LWI3MjI3Mjg2LWM3NWEtNDA2YS1iZDk1LTk1ODA3MmE1OWU5Ny5wbmcifV1dLCJhdWQiOlsidXJuOnNlcnZpY2U6ZmlsZS5kb3dubG9hZCJdfQ.CJVdW9wx7-1Oib4oEhuqPtNN6b6NgQsCv_ftvGpCbUA" style={{height: '50px'}} alt="discord"/>
            </h1>
            <p style={{fontSize: "20px", textAlign: "center"}}>Invite your friends to join room 
                <b style={{color: "red"}}> {room.charAt(0).toUpperCase() + room.slice(1)}</b> !
            </p>
            <h2 style={{textAlign: "center"}}>OR</h2>
            <p style={{fontSize: "20px", textAlign: "center"}}>Join Another Room
                <Link to='/'> <u>Here</u></Link>
            </p>

        </div>  
            <h3>Created with React, Express, Node and Socket.IO<br/>Creator: github.com/minhvuong1</h3>
    </div>
  );
};

export default TextContainer;
